import React from "react"
import "./index.scss"

function App() {
  // let count = 0;
  const [count, setCount] = React.useState(0) //инициализация значения
  const onClickPlus = () => {
    setCount(count + 1)
  }
  const onClickMinus = () => {
    setCount(count - 1)
  }

  return (
    <div className="App">
      <div>
        <h2>Счетчик:</h2>
        <h1>{count}</h1>
        <button className="minus">- Минус</button>
        <button onClink={onClickPlus} className="plus">
          Плюс +
        </button>
      </div>
    </div>
  )
}

export default App
